import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(""); // To display error messages
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post(
        "http://localhost:5000/api/auth/login",
        { email, password },
        { headers: { "Content-Type": "application/json" }, withCredentials: true }
      );
  
      console.log("Full Response:", res); 
      console.log("Response Data:", res.data); 
  
      if (res.data.token) {
        console.log("Received Token:", res.data.token);
        localStorage.setItem("token", res.data.token);
        console.log("Stored Token:", localStorage.getItem("token"));
        alert("Login Successful!");
        navigate("/dashboard");
      } else {
        alert("Login failed. No token received.");
      }
    } catch (error) {
      console.error("Login Error:", error.response?.data || error.message);
      alert("Invalid Credentials. Please try again.");
    }
  };
  

  return (
    <div className="container d-flex justify-content-center align-items-center vh-100">
      <div className="card shadow-lg p-4" style={{ width: "400px", borderRadius: "10px" }}>
        <h3 className="text-center mb-4">Login to Your Account</h3>
        {error && <div className="alert alert-danger">{error}</div>} {/* Show error messages */}
        <form onSubmit={handleLogin}>
          <div className="mb-3">
            <label className="form-label">Email</label>
            <input
              type="email"
              className="form-control"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="mb-3">
            <label className="form-label">Password</label>
            <input
              type="password"
              className="form-control"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <button type="submit" className="btn btn-success w-100">
            Login
          </button>
        </form>
        <p className="text-center mt-3">
          Don't have an account? <a href="/register" className="text-decoration-none">Sign up</a>
        </p>
      </div>
    </div>
  );
};

export default Login;
