import React, { useEffect, useState, useContext } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";

const Profile = () => {
  const { user } = useContext(AuthContext);
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    if (!user) {
      navigate("/login"); // Redirect if not logged in
      return;
    }

    const fetchProfile = async () => {
      try {
        const token = localStorage.getItem("token");
        if (!token) return;

        const res = await axios.get("http://localhost:5000/api/user", {
          headers: { Authorization: `Bearer ${token}` },
        });

        setProfile(res.data.user);
      } catch (err) {
        console.error("Error fetching profile:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchProfile();
  }, [user, navigate]);

  return (
    <div className="container mt-5">
      <h2 className="mb-4">My Profile</h2>
      {loading ? (
        <p>Loading profile...</p>
      ) : (
        <div className="card p-4 shadow-sm">
          <div className="mb-3">
            <label className="form-label">Name</label>
            <input type="text" className="form-control" value={profile?.name} disabled />
          </div>
          <div className="mb-3">
            <label className="form-label">Email</label>
            <input type="email" className="form-control" value={profile?.email} disabled />
          </div>
          <div className="mb-3">
            <label className="form-label">Role</label>
            <input type="text" className="form-control" value={profile?.role} disabled />
          </div>
          <div className="mb-3">
            <label className="form-label">Profile Picture</label>
            <img src={profile?.profilePic || "https://via.placeholder.com/100"} alt="Profile" width="100" />
          </div>
        </div>
      )}
    </div>
  );
};

export default Profile;
