import React, { useEffect, useState } from "react";
import axios from "axios";

const Dashboard = () => {
  const [donations, setDonations] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchDonations = async () => {
      try {
        const token = localStorage.getItem("token");
        if (!token) {
          setError("Unauthorized: Please log in.");
          return;
        }

        const res = await axios.get("http://localhost:5000/api/donations", {
          headers: { Authorization: `Bearer ${token}` },
        });

        setDonations(res.data.data || []); // Ensure donations is always an array
      } catch (err) {
        setError(err.response?.data?.message || "Error fetching donations.");
      }
    };

    fetchDonations();
  }, []);

  return (
    <div className="container mt-5">
      <h2>Dashboard - Your Donations</h2>

      {error ? (
        <p className="text-danger">{error}</p>
      ) : (
        <table className="table table-striped">
          <thead>
            <tr>
              <th>Food Item</th>
              <th>Quantity</th>
              <th>Status</th>
              <th>Date</th>
            </tr>
          </thead>
          <tbody>
            {donations.length > 0 ? (
              donations.map((donation) => (
                <tr key={donation._id}>
                  <td>{donation.foodItem}</td>
                  <td>{donation.quantity}</td>
                  <td>
                    <span
                      className={`badge ${
                        donation.status === "completed"
                          ? "bg-success"
                          : "bg-warning"
                      }`}
                    >
                      {donation.status}
                    </span>
                  </td>
                  <td>{donation.createdAt ? new Date(donation.createdAt).toLocaleDateString() : "N/A"}</td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="4" className="text-center">
                  No donations found.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default Dashboard;
